import {initializeApp} from 'firebase/app';
import { firebaseConfig } from './firebaseConfig.js';
import {getAuth,signInWithEmailAndPassword,createUserWithEmailAndPassword} from "firebase/auth";
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { NavLink,Link,useLocation } from 'react-router-dom';




const Login = (props) => {

    const app = initializeApp(firebaseConfig)
    const navigate  = useNavigate();
    const location  = useLocation();
    
    const page =location.pathname === "/login" ? true:false;

    const[email,setEmail] = useState("");
    const[password,setPassword] = useState("");
    const[isUserExist , setUserExist] = useState("")

    const auth  = getAuth();
    console.log(auth)

    const ctaClickHandler  = (e) =>{
        e.preventDefault();
        if(page){
            signInWithEmailAndPassword(auth,email,password)
        .then(auth =>{
            if(auth){
                navigate('./dashboard')
            }
        })
        .catch(error=>{
            alert(error.message)
            setUserExist("user does not exist")
     } )
        setEmail("")
        setPassword("")
        setUserExist("")

        }
        else{

            createUserWithEmailAndPassword(auth,email,password)
            .then(auth =>{
                if(auth){
                    navigate('./dashboard')
                }
            })
            .catch(error=>{
                alert(error.message)
                setUserExist("user already exist")
         } )
         setEmail("")
         setPassword("")
         setUserExist("")

        }

        
        
        
        }
    


    const emailOnChangeHandler = (e) =>{
        return setEmail(e.target.value)

    }

    const passwordOnChangeHandler = (e) =>{
        return setPassword(e.target.value)

    }

    return <>
        <div className="login">
            <div className="holder">
                <h1 className="text-white">{props.page} </h1>
                <br />
                <form>
                    <input
                        className="form-control"
                        type="email"
                        value={email}
                        onChange={emailOnChangeHandler}
                        placeholder="Email" />
                    <input
                        className="form-control"
                        type="password"
                        value={password}
                        onChange={passwordOnChangeHandler}
                        placeholder="Password" />
                    <p className="text-danger">Password is invalid/blank</p>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                        <label className="form-check-label text-white" htmlFor="flexCheckDefault">
                            Remember Me
                        </label>
                    </div>
                    <button className="btn btn-danger btn-block" onClick={ctaClickHandler}>
                        {props.page}
                    </button>
                    <br />
                </form>
                {}
                <p className='error-msg'>{isUserExist?isUserExist:""}</p>
                
                {/* { isUserExist && <p className="text-danger">User does not exist | Go for Signup</p> }
        { isEmailUsed && <p className="text-danger">Email already in use | Go for Sign In</p> } */}
                <div className="login-form-other">
                    <div className="login-signup-now">
                        New to Netflix? <Link to={props.page=="register"?"/login":"/register"}>{props.page=="register"?"sign-in":"register"}</Link>
                    </div>
                </div>
            </div>
            <div className="shadow"></div>
            <img className="concord-img vlv-creative" src="https://assets.nflxext.com/ffe/siteui/vlv3/6e32b96a-d4be-4e44-a19b-1bd2d2279b51/ee068656-14b9-4821-89b4-53b4937d9f1c/IN-en-20220516-popsignuptwoweeks-perspective_alpha_website_small.jpg" alt="" />
        </div>
    </>
}

export default Login;
