import { useEffect, useState } from "react";
import axios from "axios";


const URL = "https://api.themoviedb.org/3/movie/top_rated";
const API_KEY = "78f55253c26e406f891a00ef0426e731";

const Banner = () => {
    


    const [list, setList] = useState([]);

    
    useEffect(()=>{

      const fetchData = async() => {
        const response = await axios.get(`${URL}?api_key=${API_KEY}`)
        setList(response.data.results)
        return response;
    }
       
       fetchData();
    },[]);
    console.log(list);


    // let im = list.map((Ele)=>{
    //   return Ele.backdrop_path
    // })
    const im = () =>{
       return list.map((Ele)=>{
        return Ele.backdrop_path
      })
    }
     
    console.log(im)
    let ran;
    ran = setInterval(()=>{

      // return im[Math.floor(Math.random()*19)]
    let m =  im();
      
    },1000)

    console.log(ran)

    return(
      <div className="banner" style={{backgroundImage:`url("https://image.tmdb.org/t/p/original/${ran}")`}}>
        <div className="banner__contents">
          <h1 className="banner__title"></h1>
          <div className="banner__buttons">
            <button className="banner__button">Play</button>
            <button className="banner__button">My List</button>
          </div>
          <h1 className="banner__description">
            Angsty and awkward fifteen year old Ginny Miller often feels more
            mature than her thirty year old mother, the irresistible and dynamic
            Georgia Miller...
          </h1>
        </div>
        <div className="banner--fadeBottom"></div>
      </div>
    )
  }
  
  export default Banner;
