import { useEffect, useState } from "react";
import axios from "axios";


const URL = "https://api.themoviedb.org/3/movie/550";
const API_KEY = "78f55253c26e406f891a00ef0426e731";

const Banner = () => {
    


    const [list, setList] = useState([]);

    const fetchData = () => {
        return axios.get(`${URL}?api_key=${API_KEY}`)
    }
    useEffect(()=>{
      fetchData().then( res => setList(res.data.results))
    },[]);
    console.log(list);

    // const img = list.map((Ele)=>{
    //   return Ele
    // })
    // console.log(img)
    return(
      <div className="banner" style={{backgroundImage:`url("https://image.tmdb.org/t/p/original/")`}}>
        <div className="banner__contents">
          <h1 className="banner__title">Ginny &amp; Georgia</h1>
          <div className="banner__buttons">
            <button className="banner__button">Play</button>
            <button className="banner__button">My List</button>
          </div>
          <h1 className="banner__description">
            Angsty and awkward fifteen year old Ginny Miller often feels more
            mature than her thirty year old mother, the irresistible and dynamic
            Georgia Miller...
          </h1>
        </div>
        <div className="banner--fadeBottom"></div>
      </div>
    )
  }
  
  export default Banner;
